  `product_id` INTEGER(4) NOT NULL,
  `month_sold` DATE NOT NULL,
  `location_id` TINYTEXT,
  `sold_qty` INTEGER(11) NOT NULL,
  `sold_amt` DECIMAL(11,2) DEFAULT NULL,
  `tax_amt` INTEGER(11) DEFAULT NULL,
  `tax_calc` TEXT COLLATE latin1_swedish_ci,
  `returned_qty` INTEGER(11) DEFAULT NULL,
  `returned_amt` INTEGER(11) DEFAULT NULL,
  `tax_return_amt` INTEGER(11) DEFAULT NULL,
  `tax_adjust_amt` INTEGER(11) DEFAULT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `id` (`id`)
)ENGINE=InnoDB
AUTO_INCREMENT=101 AVG_ROW_LENGTH=163 CHARACTER SET 'latin1' COLLATE 'latin1_swedish_ci';
COMMIT;



